def isComposite(n):
	if (n <= 1):
		return False
	if (n <= 3):
		return False
	if (n % 2 == 0 or n % 3 == 0):
		return True
	i = 5
	while(i * i <= n):
		if (n % i == 0 or n % (i + 2) == 0):
			return True
		i += 6
	return False

def sumOfSubArray(arr, L, R, budget):
	sum = 0
	compositeCount = 0
	for i in range(L, R + 1):
		if (isComposite(arr[i])):
			compositeCount += 1
		else:
			sum += arr[i]
	if compositeCount <= budget:
		return sum/(R-L+1)
	else:
		return "Excluded due to budget constraints"

