def findNumbers(N: int, K: int):
    if N == 0 or K == 0:
        return [0] * K
    res = [0] * K
    for i in range(K):
        res[i] = (N - sum(res)) // (K - i)
    for i in range(K - 1, -1, -1):
        while res[i] * res[i] > (N - sum(res) + res[i]) ** 2:
            res[i] -= 1
    return res

