def calculate_all_subsets(prefix_sum, n):
    all_subset_sums = set()
    for i in range(1, n+1):
        for j in range(i, n+1):
            all_subset_sums.add(prefix_sum[j] - prefix_sum[i-1])
    return min([max(all_subset_sums) - min(all_subset_sums), 0])

