def findCombinations(arr, target):
    result = []
    arr.sort()
    def backtrack(remain, comb, start):
        if remain == 0:
            result.append(list(comb))
            return
        elif remain < 0:
            return
        for i in range(start, len(arr)):
            if i > start and arr[i] == arr[i-1]:
                continue
            comb.append(arr[i])
            backtrack(remain - arr[i], comb, i + 1)
            comb.pop()
    backtrack(target, [], 0)
    return result

