def minimizeDonationCost(costs: List[int], num_processors: int, num_causes: int) -> int:
    if num_processors >= num_causes:
        return sum(costs)

    heapq.heapify(costs)
    total_cost = 0
    for _ in range(num_causes // num_processors):
        temp = 0
        for _ in range(num_processors):
            temp += heapq.heappop(costs)
