			if (i == 0 or j == 0):
				dp[i][j] = 0
			elif (arr1[i - 1] == arr2[j - 1]):
				dp[i][j] = 1 + dp[i - 1][j - 1]
			else:
				dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])
	return dp[n][m]

from typing import List
def generateArray(n: int) -> List[int]:
    arr = []

    if n % 2 != 0:
        for i in range(1, n):
            arr.append(i)
        arr.append((-sum(arr)) % n)
    else:
        for i in range(1, n):
            arr.append(i)
        arr[n-1] = (n*(n-1)//2 - sum(arr)) % n

    return arr

