def medianOfDifferenceOfPairs(arr: List[int]) -> float:
    n = len(arr)
    arr.sort()
    mid = (n * (n - 1)) // 2
    vec = []
    for i in range(n-1):
        for j in range(i+1, n):
            vec.append(arr[j]-arr[i])
    vec.sort()
    if mid % 2 == 1:
        return float(vec[mid])
    return float((vec[mid] + vec[mid+1]) / 2)

