from typing import List
def longestSubarray(arr: List[int]) -> int:
    n = len(arr)
    res,product = 0,1
    j = 0
    for i in range(n):
        product *= arr[i]
        while(product % 2 != 0 and j < i + 1):
            product //= arr[j]
            j += 1
        if(product % 2 == 0):
            res = max(res, i - j + 1)

    return res

def maxSubSumSwap(A: List[int], B: List[int], n: int) -> int:
    max_sum = max(A)
    min_B = min(B)
    idx = B.index(min_B)
    if min_B < 0:
        return max_sum
    else:
        diff = max_sum - min_B
        if diff <= max_sum:
            A[A.index(max_sum)] = min_B
            B[idx] = max_sum
            return sum(A)
        else:
            return max_sum

from typing import List
def maxSubsetSumDivisibleByK(arr: List[int], k: int) -> int:
    n = len(arr)
    dp = [-1] * k
    dp[0] = 0
    for num in arr:
        new_dp = dp[:]
        for i in range(k):
            if dp[i] >= 0:
                new_dp[(i + num) % k] = max(new_dp[(i + num) % k], dp[i] + num)
        dp = new_dp
    return dp[0]

def maximizeArrayElements(arr, k):
    for i in range(len(arr)):
        if arr[i] < k - arr[i]:
            arr[i] += k
        else:
            arr[i] -= k
    return arr

from typing import List
def sumOfAllPairsWithSkip(arr: List[int], N: int) -> int:
    total_sum = 0

    # sum of all pairs without skip
    for i in range(N):
        for j in range(i+1, N):
            total_sum += (arr[i] + arr[j])

    # sum of all pairs with skip
    for i in range(N):
        for j in range(i+2, N):
            total_sum += (arr[i] + arr[j])

    return total_sum
