def findNonRepeating(arr: List[int]) -> List[int]:
    xor = 0
    for num in arr:
        xor ^= num

    # Get the rightmost set bit in xor
    rightmost_set_bit = xor & -xor

    # Divide the array into two groups based on the rightmost set bit
    x = 0
    y = 0
    for num in arr:
        if num & rightmost_set_bit:
            x ^= num
        else:
            y ^= num

    return [x, y]

