def leastInterval(tasks: List[str], n: int) -> int:
        hmap = {}
        for task in tasks:
            if task in hmap:
                hmap[task] += 1
            else:
                hmap[task] = 1

        queue = []
        for task, freq in hmap.items():
            heapq.heappush(queue, (-freq, task))

        result = 0
        while queue:
            k = n + 1
            temp = []
            while k > 0 and queue:
                freq, task = heapq.heappop(queue)
                result += 1
                k -= 1
                if freq + 1 < 0:
                    temp.append((freq + 1, task))

            if temp:
                result += k
            for item in temp:
                heapq.heappush(queue, item)
        return result

