class MaxStack:

    def __init__(self):
        self.stack = []


    def push(self, x: int) -> None:
        if not self.stack:
            self.stack.append((x,x))
        else:
            self.stack.append((x,max(x,self.stack[-1][1])))


    def pop(self) -> None:
        if self.stack:
            self.stack.pop()


    def top(self) -> int:
        if self.stack:
            return self.stack[-1][0]
        else:
            return None


    def peekMax(self) -> int:
        if self.stack:
            return self.stack[-1][1]
        else:
            return None


    def popMax(self) -> int:
        if self.stack:
            max_val = self.stack[-1][1]
            buffer = []
            while self.stack[-1][0] != max_val:
                buffer.append(self.stack.pop()[0])
            self.stack.pop()
            for val in buffer[::-1]:
                self.push(val)
            return max_val
        else:
            return None


