def gcd(num1: int, num2: int) -> int:
    while num2:
        num1, num2 = num2, num1 % num2
    return num1

def gcd(a: int, b: int) -> int:
    while b:
        a, b = b, a % b
    return a

def lcm(a: int, b: int) -> int:
    return (a * b) // gcd(a, b)

def gcd_lcm(a: int, b: int) -> (int, int):
    return (gcd(a, b), lcm(a, b))

