    dfs(adj, 1, -1, subtree_sizes)

    result = [0]

    def dfs2(v: int, parent: int, size: int) -> int:
        nonlocal result
        children_sum = 0
        for u in adj[v]:
            if u == parent:
                continue
            children_sum += subtree_sizes[u]
            dfs2(u, v, size)
        if 2 * children_sum <= size and 2 * (size - subtree_sizes[v]) <= size and not 2 * subtree_sizes[v] == size:
            result[0] += 1
        return subtree_sizes[v]

    dfs2(1, -1, n)
    return result[0]

