    all_weights = sorted([w for level in weights for w in level])
    res = []
    for i in range(1, X+1):
        found = False
        for w in all_weights:
            if w <= i and res[i-w] != -1:
                res.append(w)
                found = True
                break
        if not found:
            res.append(-1)
    return res

