def maxDiagonalDifference(matrix: list) -> int:
    max_diff = 0
    n = len(matrix)
    for i in range(n):
        for j in range(i+1, n):
            if matrix[i][j] == None:
                matrix[i][j] = 0
            if matrix[j][i] == None:
                matrix[j][i] = 0
            diff = abs(matrix[i][i] - matrix[j][i])
            max_diff = max(max_diff, diff)
    return max_diff

def minOperationsToTargetAmount(denominations, target):
    dp = [-1] * (target + 1)
    dp[0] = 0
    for i in range(1, target + 1):
        for coin in denominations:
            if i >= coin and dp[i - coin] != -1:
                if dp[i] == -1 or dp[i] > dp[i - coin] + 1:
                    dp[i] = dp[i - coin] + 1
    return dp[target]

