def maxPossibleGCDWithReplacement(arr):
    n = len(arr)
    maxGCD = 0
    maxGCDIndex = -1
    for i in range(n):
        if arr[i] > maxGCD:
            maxGCD = arr[i]
            maxGCDIndex = i
    newMaxGCD = 0
    for i in range(n):
        if i != maxGCDIndex:
            newMaxGCD = max(newMaxGCD, gcd(maxGCD, arr[i]))
    finalGCD = newMaxGCD & maxGCD
    return finalGCD

