def findMinStepsToComplement(numbers):
    def countSetBits(n):
        count = 0
        while n:
            count += n & 1
            n >>= 1
        return count

    steps = 0
    for num in numbers:
        setBits = countSetBits(num)
        steps += max(1, setBits)

    return steps

def distinctSubsequenceSum(arr, K):
    distinct_subsequences = set()
