def maxPointsSameCircle(points: List[List[int]]) -> int:
    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def simplify(dx, dy):
        if dx == 0:
            return (0, 1)
        if dy == 0:
            return (1, 0)
        if dx < 0:
            dx, dy = -dx, -dy
        g = gcd(abs(dx), abs(dy))
        return (dx // g, dy // g)

    n = len(points)
    maxPoints = 0
    for i in range(n):
        slopes = {}
        same = 0
        for j in range(n):
            if i != j:
                x1, y1 = points[i]
                x2, y2 = points[j]
                if x1 == x2 and y1 == y2:
                    same += 1
                else:
                    dx, dy = simplify(x2 - x1, y2 - y1)
                    slopes[(dx, dy)] = slopes.get((dx, dy), 0) + 1
        maxPoints = max(maxPoints, max(slopes.values(), default=0) + same)
    return maxPoints + 1 if maxPoints > 0 else 0

